// Configuration
const imapConfig = {
  host: '{{$node["Credentials"].json["imapHost"]}}',
  port: {{$node["Credentials"].json["imapPort"]}},
  user: '{{$node["Credentials"].json["email"]}}',
  password: '{{$node["Credentials"].json["password"]}}',
  tls: true
};

// Filter settings
const filters = {
  projectKeywords: ['Secret Trees', 'Carbon', 'Tokenization', 'Eco-tourism', 'Hempcrete', 'Latvia'],
  propertyKeywords: ['Property', 'Garden', 'Construction', 'Land', 'Real Estate'],
  familyKeywords: ['Family', 'Personal', 'Health', 'Travel'],
  timeManagementKeywords: ['Schedule', 'Meeting', 'Appointment', 'Deadline', 'Calendar', 'Event', 'Due date']
};

// Function to classify email by content
function classifyEmail(email) {
  const subject = email.subject?.toLowerCase() || '';
  const body = email.html?.toLowerCase() || email.text?.toLowerCase() || '';

  // Check project-related
  if (filters.projectKeywords.some(keyword => 
      subject.includes(keyword.toLowerCase()) || 
      body.includes(keyword.toLowerCase()))) {
    return 'project';
  }

  // Check property-related
  if (filters.propertyKeywords.some(keyword => 
      subject.includes(keyword.toLowerCase()) || 
      body.includes(keyword.toLowerCase()))) {
    return 'property';
  }

  // Check family-related
  if (filters.familyKeywords.some(keyword => 
      subject.includes(keyword.toLowerCase()) || 
      body.includes(keyword.toLowerCase()))) {
    return 'family';
  }

  // Check time management
  if (filters.timeManagementKeywords.some(keyword => 
      subject.includes(keyword.toLowerCase()) || 
      body.includes(keyword.toLowerCase()))) {
    return 'timeManagement';
  }

  // Default classification
  return 'other';
}

// Implement the fetch emails function
async function fetchAndClassifyEmails() {
  const Imap = require('imap');
  const {simpleParser} = require('mailparser');

  return new Promise((resolve, reject) => {
    try {
      const imap = new Imap(imapConfig);

      const emails = {
        project: [],
        property: [],
        family: [],
        timeManagement: [],
        other: []
      };

      function openInbox(cb) {
        imap.openBox('INBOX', true, cb);
      }

      imap.once('ready', function() {
        openInbox(function(err, box) {
          if (err) throw err;

          // Get emails from the last 24 hours
          const yesterday = new Date();
          yesterday.setDate(yesterday.getDate() - 1);

          imap.search(['UNSEEN', ['SINCE', yesterday]], function(err, results) {
            if (err) throw err;

            if (!results || !results.length) {
              console.log('No new emails');
              imap.end();
              return resolve(emails);
            }

            const f = imap.fetch(results, {bodies: '', markSeen: false});

            f.on('message', function(msg, seqno) {
              const email = {};

              msg.on('body', function(stream, info) {
                let buffer = '';
                stream.on('data', function(chunk) {
                  buffer += chunk.toString('utf8');
                });

                stream.on('end', function() {
                  simpleParser(buffer, (err, parsed) => {
                    if (err) {
                      console.error('Parser error:', err);
                    } else {
                      email.from = parsed.from?.text;
                      email.subject = parsed.subject;
                      email.date = parsed.date;
                      email.text = parsed.text;
                      email.html = parsed.html;
                      email.id = parsed.messageId;

                      // Mark the source account
                      email.account = '{{$node["Credentials"].json["accountName"]}}';
                    }
                  });
                });
              });

              msg.once('end', function() {
                const category = classifyEmail(email);
                email.category = category;
                emails[category].push(email);

                // If this email has time management content, add to that category too
                if (category !== 'timeManagement' && 
                   filters.timeManagementKeywords.some(keyword => 
                     (email.subject?.toLowerCase() || '').includes(keyword.toLowerCase()) || 
                     (email.text?.toLowerCase() || '').includes(keyword.toLowerCase()))) {
                  emails.timeManagement.push(email);
                }
              });
            });

            f.once('error', function(err) {
              console.error('Fetch error:', err);
              reject(err);
            });

            f.once('end', function() {
              console.log('Done fetching messages');
              imap.end();
            });
          });
        });
      });

      imap.once('error', function(err) {
        console.error('IMAP error:', err);
        reject(err);
      });

      imap.once('end', function() {
        console.log('IMAP connection ended');
        resolve(emails);
      });

      imap.connect();
    } catch (error) {
      console.error('Error in email processing:', error);
      reject(error);
    }
  });
}

// Summarize email content
function summarizeEmail(text, maxLength = 250) {
  if (!text) return 'No content available';

  // Simple summary: just truncate
  if (text.length <= maxLength) return text;

  return text.substring(0, maxLength) + '...';
}

// Extract time-sensitive information
function extractTimeInfo(email) {
  const timeInfo = {
    dates: [],
    times: [],
    keywords: []
  };

  // Simple extraction of obvious time markers
  const text = email.text || email.html || '';

  // Extract dates (simple approach)
  const dateMatches = text.match(/\d{1,2}[\/\-\.]\d{1,2}[\/\-\.]\d{2,4}/g) || [];
  timeInfo.dates = dateMatches;

  // Extract times
  const timeMatches = text.match(/\d{1,2}:\d{2}(?:\s*)?(am|pm)?/gi) || [];
  timeInfo.times = timeMatches;

  // Look for time-related keywords
  filters.timeManagementKeywords.forEach(keyword => {
    if (text.toLowerCase().includes(keyword.toLowerCase())) {
      timeInfo.keywords.push(keyword);
    }
  });

  return timeInfo;
}

// Format emails for template
function formatEmailsForTemplate(emails) {
  const result = {
    projectEmails: [],
    propertyEmails: [],
    familyEmails: [],
    timeManagementEmails: [],
    actionItems: [],
    insights: ''
  };

  // Process project emails
  if (emails.project && emails.project.length) {
    result.projectEmails = emails.project.map(email => ({
      subject: email.subject || 'No Subject',
      from: email.from || 'Unknown Sender',
      date: email.date ? new Date(email.date).toLocaleString() : 'Unknown Date',
      category: email.category,
      summary: summarizeEmail(email.text || email.html),
      emailId: email.id || '#',
      account: email.account
    }));

    // Extract potential action items
    emails.project.forEach(email => {
      const text = email.text || '';
      const lines = text.split('\n');

      lines.forEach(line => {
        if (/todo|action item|deadline|required|urgent/i.test(line)) {
          result.actionItems.push(line.trim());
        }
      });
    });
  }

  // Process property emails
  if (emails.property && emails.property.length) {
    result.propertyEmails = emails.property.map(email => ({
      subject: email.subject || 'No Subject',
      from: email.from || 'Unknown Sender',
      date: email.date ? new Date(email.date).toLocaleString() : 'Unknown Date',
      summary: summarizeEmail(email.text || email.html),
      account: email.account
    }));
  }

  // Process family emails
  if (emails.family && emails.family.length) {
    result.familyEmails = emails.family.map(email => ({
      subject: email.subject || 'No Subject',
      from: email.from || 'Unknown Sender',
      date: email.date ? new Date(email.date).toLocaleString() : 'Unknown Date',
      summary: summarizeEmail(email.text || email.html),
      account: email.account
    }));
  }

  // Process time management emails
  if (emails.timeManagement && emails.timeManagement.length) {
    result.timeManagementEmails = emails.timeManagement.map(email => {
      const timeInfo = extractTimeInfo(email);
      return {
        subject: email.subject || 'No Subject',
        from: email.from || 'Unknown Sender',
        date: email.date ? new Date(email.date).toLocaleString() : 'Unknown Date',
        summary: summarizeEmail(email.text || email.html),
        dates: timeInfo.dates,
        times: timeInfo.times,
        keywords: timeInfo.keywords,
        account: email.account
      };
    });
  }

  // Generate simple insights
  const totalEmails = emails.project.length + emails.property.length + emails.family.length + emails.timeManagement.length + emails.other.length;
  result.insights = `Today's digest contains ${totalEmails} new emails: ${emails.project.length} project-related, ${emails.property.length} property-related, ${emails.family.length} family-related, and ${emails.timeManagement.length} time-sensitive.`;

  return result;
}

// Main execution
async function main() {
  try {
    // Fetch and classify emails
    const emails = await fetchAndClassifyEmails();

    // Format emails for template
    const templateData = formatEmailsForTemplate(emails);

    // Return the formatted data
    return templateData;
  } catch (error) {
    console.error('Error in email digest workflow:', error);
    return { error: error.message };
  }
}

// Execute the main function
return main();

