// Prepare data for creating the email digest
const today = new Date().toISOString().split('T')[0];
const emailData = $input.json;

// Generate HTML content with Handlebars-like template
function generateHtml(data) {
  // Simple template rendering function
  function render(template, context) {
    // Handle if/else blocks
    template = template.replace(/{{#if ([^}]+)}}/g, (match, condition) => {
      const value = condition.split('.').reduce((obj, prop) => obj?.[prop], context);
      return value && value.length ? '' : '<!-- if-removed -->';
    });

    template = template.replace(/{{else}}/g, () => {
      return '<!-- else -->';
    });

    template = template.replace(/{{\/#if}}/g, () => {
      return '<!-- endif -->';
    });

    // Process each blocks
    template = template.replace(/{{#each ([^}]+)}}/g, (match, arrayName) => {
      const array = arrayName.split('.').reduce((obj, prop) => obj?.[prop], context);
      if (!array || !array.length) return '';

      // Find the end of this each block
      const startPos = template.indexOf(match);
      const endPos = template.indexOf('{{/each}}', startPos);

      if (endPos === -1) return '';

      const blockContent = template.substring(startPos + match.length, endPos);
      let result = '';

      array.forEach(item => {
        let itemContent = blockContent;
        // Replace variables in the block
        itemContent = itemContent.replace(/{{([^}]+)}}/g, (m, varName) => {
          return item[varName] || '';
        });
        result += itemContent;
      });

      return result;
    });

    // Clean up each closing tags
    template = template.replace(/{{\/#each}}/g, '');

    // Replace regular variables
    template = template.replace(/{{([^}]+)}}/g, (match, varName) => {
      if (varName.startsWith('date:')) {
        const format = varName.substring(5);
        return today; // Format date as needed
      }
      return varName.split('.').reduce((obj, prop) => obj?.[prop] ?? '', context);
    });

    // Clean up removed if blocks
    let cleanTemplate = '';
    let skipSection = false;
    const lines = template.split('\n');

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      if (line.includes('<!-- if-removed -->')) {
        skipSection = true;
        continue;
      }

      if (skipSection && line.includes('<!-- else -->')) {
        skipSection = false;
        continue;
      }

      if (line.includes('<!-- endif -->')) {
        skipSection = false;
        continue;
      }

      if (!skipSection && !line.includes('<!-- else -->') && !line.includes('<!-- endif -->')) {
        cleanTemplate += line + '\n';
      }
    }

    return cleanTemplate;
  }

  // Template content (would be fetched from a file in production)
  const template = `---
security: team
tags: [email-digest, automation, time-management]
date: {{date:YYYY-MM-DD}}
---

# Email Digest: {{date:YYYY-MM-DD}}

## Time Management
{{#if timeManagementEmails}}
### Time-Sensitive Items
{{#each timeManagementEmails}}
#### {{subject}}
**From:** {{from}}
**Date:** {{date}}
**Time Markers:** {{#if dates}}{{dates}}{{/if}}{{#if times}} at {{times}}{{/if}}

{{summary}}

---
{{/each}}
{{else}}
*No time-sensitive items in this period*
{{/if}}

## Secret Trees Project Updates
{{#if projectEmails}}
{{#each projectEmails}}
### {{subject}}
**From:** {{from}}
**Date:** {{date}}
**Category:** {{category}}

{{summary}}

---
{{/each}}
{{else}}
*No project updates in this period*
{{/if}}

## Property Management
{{#if propertyEmails}}
{{#each propertyEmails}}
### {{subject}}
**From:** {{from}}
**Date:** {{date}}

{{summary}}

---
{{/each}}
{{else}}
*No property management emails in this period*
{{/if}}

## Family & Personal
{{#if familyEmails}}
{{#each familyEmails}}
### {{subject}}
**From:** {{from}}
**Date:** {{date}}

{{summary}}

---
{{/each}}
{{else}}
*No family emails in this period*
{{/if}}

## Action Items
{{#if actionItems}}
{{#each actionItems}}
- [ ] {{this}}
{{/each}}
{{else}}
*No action items identified*
{{/if}}

## Insights
{{#if insights}}
{{insights}}
{{else}}
*No insights for this period*
{{/if}}`;

  return render(template, data);
}

// Generate the digest content
const digestContent = generateHtml(emailData);
const filename = `email-digest-${today}.md`;

return {
  file: filename,
  content: digestContent,
  operation: 'create'
};

